class Circe {
    constructor(radius) {
        this.radius = radius;
    }
    get circleRadius() {
        return `radius is ${this.radius} mm`;
    }
    set circleRadius(radius) {
        this.radius = radius; // тут работает, но не пойму всех связей
    }
    get circleDiam() {
        return `diameter is ${this.radius * 2} mm`;
    }
}
// step 1 - get radius 
const circle = new Circe(150);
console.log(circle.circleRadius);
// step 2 - change radius 
circle.radius = 600;
console.log(circle.circleRadius);
// step 3 - calculate diameter
console.log(circle.circleDiam);
